INSERT INTO CODELIST (LIST_NAME, CODE, SHORT_NAME, DESCRIPTION, CREATED_BY, CREATED_DATE, LAST_MODIFIED_BY, LAST_MODIFIED_DATE)
values ('GDPR_ARTICLE', 'ART61A', 'Art. 6(1)a - Samtykke', 'En virksomhet kan behandle personopplysninger dersom den har innhentet gyldig samtykke fra personen eller personene det gjelder.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART61B', 'Art. 6(1)b - Avtale', 'En virksomhet kan behandle personopplysninger dersom det er nødvendig for å oppfylle en avtale som den det gjelder er part i. Det samme gjelder der behandlingen er nødvendig for å gjennomføre tiltak som den enkelte har spurt etter før avtaleinngåelse.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART61C', 'Art. 6(1)c - Rettslig forpliktelse', 'En virksomhet kan behandle personopplysninger dersom det er nødvendig for å oppfylle en rettslig forpliktelse. Med andre ord: Dersom virksomheten er pålagt å behandle visse bestemte personopplysninger, har den også lov til det. Den rettslige forpliktelsen må følge av hjemmel i nasjonal lov eller forskrift som NAV er underlagt.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART61D', 'Art. 6(1)d - Vitale interesser', 'En virksomhet kan behandle personopplysninger dersom det er nødvendig for å beskytte den registrertes eller en annen persons vitale interesser. Dette behandlingsgrunnlaget er svært snevert. Det må være snakk om behandling av personopplysninger i forbindelse med liv og død eller fare for helse.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART61E', 'Art. 6(1)e - Allmennhetens interesse eller utøve offentlig myndighet', 'En virksomhet kan behandle personopplysninger dersom det er nødvendig for å utføre en oppgave i allmennhetens interesse. Det samme gjelder dersom behandlingen er nødvendig for å utøve offentlig myndighet som den behandlingsansvarlige er pålagt. Behandlingen må følge av hjemmel i nasjonal lov eller forskrift.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART61F', 'Art. 6(1)f - Berettiget interesse', 'En virksomhet kan behandle personopplysninger dersom det er nødvendig for å vareta en berettiget interesse som veier tyngre enn hensynet til den enkeltes personvern. NB! Kan ikke brukes som behandlingsgrunnlag for behandling av personopplysninger som ledd i å utøve offentlig myndighet. Men den kan brukes for annen type behandling, som for eksempel behandling av personopplysninger som arbeidsgiver.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART92A', 'Art. 9(2)a - Samtykke', 'En virksomhet kan behandle særlige kategorier av personopplysninger dersom det er innhentet et gyldig samtykke fra personen det gjelder.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART92B', 'Art. 9(2)b - Arbeidsrett, trygderett og sosialrett', 'Unntaket gir en virksomhet rett til å behandle særlige kategorier personopplysninger når det er nødvendig for å oppfylle forpliktelser på områdene arbeidsrett, trygderett og sosialrett. Dette unntaket brukes for eksempel for å behandle opplysninger om sykefravær for ansatte, og for å behandle opplysninger om funksjonsnedsettelser på hjelpemiddelområdet.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART92C', 'Art. 9(2)c - Vitale interesser', 'Dette grunnlaget kan brukes når det er nødvendig å behandle særlige kategorier av personopplysninger for å verne en interesse som er av avgjørende betydning for den registrerte eller en annen fysisk persons liv. Det kan også benyttes dersom den registrerte fysisk eller juridisk ikke er i stand til å gi samtykke. Unntaket bør bare brukes dersom det er åpenbart at de andre grunnlagene ikke kan brukes.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART92D', 'Art. 9(2)d - Stiftelse, sammenslutning eller ideelt organ', 'Grunnlaget kan brukes når behandlingen utføres av en stiftelse, sammenslutning eller et annet ideelt organ hvis mål er av politisk, religiøs eller fagforeningsmessig art, som ledd i organets berettigede aktiviteter og med nødvendige garantier, forutsatt at behandlingen bare gjelder organets medlemmer eller tidligere medlemmer eller personer som på grunn av organets mål har regelmessig kontakt med det, og at personopplysningene ikke utleveres til andre enn nevnte organ uten de registrertes samtykke.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART92E', 'Art. 9(2)e - Offentlige opplysninger', 'Dette grunnlaget brukes når det er åpenbart at den registrerte har offentliggjort opplysningene selv.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART92F', 'Art. 9(2)f - Rettskrav', 'Unntaket gir en virksomhet rett til å behandle særlige kategorier av personopplysninger når det er nødvendig for å fastsette, gjøre gjeldende eller forsvare et rettskrav. Dette vil typisk være tilfelle der det oppstår en rettssak.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART92G', 'Art. 9(2)g - Viktige allmenne interesser', 'Behandling av særlige kategorier kan også skje når det er nødvendig av hensyn til viktige allmenne interesser og det er fastsatt i norsk lov at opplysningene kan eller skal behandles. Dette kan brukes for eksempel som grunnlag for å kunne arkivere særlige opplysninger.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART92H', 'Art. 9(2)h - Medisin', 'Unntaket gir en virksomhet rett til å behandle særlige kategorier av personopplysninger når det er nødvendig i forbindelse med arbeidsmedisin for å vurdere en arbeidstakers arbeidskapasitet eller forvaltning av helse- og sosialtjenester.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART92I', 'Art. 9(2)i - Allmenne folkehelsehensyn', 'En virksomhet kan behandle særlige kategorier av personopplysninger når det er nødvendig av allmenne folkehelsehensyn. Eksempler:\n• vern mot alvorlige grenseoverskridende helsetrusler\n• høye kvalitets- og sikkerhetsstandarder for helsetjenester og legemidler eller medisinsk utstyr\n• egnede og særlige tiltak for å verne den registrertes rettigheter og friheter på grunnlag av unionsretten eller medlemsstatenes nasjonale rett\n• særlig taushetsplikt', 'Datajegerne', now(), 'Datajegerne', now()),
       ('GDPR_ARTICLE', 'ART92J', 'Art. 9(2)j - Arkivformål', 'En virksomhet kan behandle særlige kategorier av personopplysninger når det er nødvendig for arkivformål i allmennhetens interesse, for formål knyttet til vitenskapelig eller historisk forskning, eller for statistiske formål når det følger av norsk lov.', 'Datajegerne', now(), 'Datajegerne', now()),
       ('SENSITIVITY', 'POL', 'Personopplysning', 'Personopplysning – GDPR art. 6', 'Datajegerne', now(), 'Datajegerne', now()),
       ('SENSITIVITY', 'SÆRLIGE', 'Særlige personopplysninger', 'Særlige kategorier av personopplysninger – GDPR art. 9', 'Datajegerne', now(), 'Datajegerne', now()),
       ('SENSITIVITY', 'STRAFF', 'Straffedommer og lovovertredelser', 'Straffedommer og lovovertredelser – GDPR art. 10', 'Datajegerne', now(), 'Datajegerne', now());
